package day1.basic;

public class LoopingStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte Max = 55;
		byte Min = 28;
		
		System.out.println("Min value of byte is" +Min+"Max range of byte is "+Max);
		
		//short - 2 bytes
		short a = 2567;
		short b = 2358;
		System.out.println("Minshort range of byte is" +
				a+"Maxshort range of byte is "+b);
		
		//int - 4bytes
		int maxint = 100;
		int minint = 99;
		System.out.println("Minint range of byte is" +minint+"Maxint range of byte is "+maxint);
		
		//long - 8bytes
		long num1 = 123456789L;
		long num2 = -32145678L;
		
		System.out.println("Minlong range of byte is" +
				num2+"Maxlong range of byte is "+num1);
		
		float c=25.06f;
		double d=88.99f;
		System.out.println("float value is "+c+" double value is "+d);
		
		//boolean 
		boolean bird=false;
		System.out.println("boolean value is "+bird);

	}
}



  